#nullable enable

namespace Corvus.Storage
{
    /// <summary>
    /// A stored entity with an associated eTag generated by a store.
    /// </summary>
    /// <typeparam name="T">Entity type.</typeparam>
    /// <param name="Entity">The stored entity.</param>
    /// <param name="ETag">The eTag.</param>
    public readonly record struct EntityWithETag<T>(T Entity, string? ETag);
}